jQuery(window).on('elementor/frontend/init', () => {
    const addHandler = ($element) => {
        jQuery('body')
            .find('.santoi-shape-dividers')
            .each(function () {
                if (!jQuery(this).hasClass('loaded')) {
                    let divider = new dividerShapes(this);
                    divider.initPoints();
                    jQuery(this).addClass('loaded');
                }
            });
    };

    elementorFrontend.hooks.addAction(
        'frontend/element_ready/santoi-dividers.default',
        addHandler
    );
});


jQuery(window).on('elementor/frontend/init', () => {
    const addSectionHandler = ( $element ) => {
        var attr = $element.attr('data-santoi-dividers');
        if (typeof attr !== 'undefined' && attr !== false) {
            let isCol = $element.hasClass('elementor-column');
            if(isCol){
                $element.find('> .elementor-column-wrap').addClass('santoi-elementor-section-dividers')
                $element.find('> .elementor-column-wrap').prepend($element.attr('data-santoi-dividers'));
            }else{
                $element.addClass('santoi-elementor-section-dividers')
                $element.prepend($element.attr('data-santoi-dividers'));
            }
            $element.attr('data-santoi-dividers', '');
                    $element
                        .find('.santoi-shape-dividers')
                        .each(function () {
                            if (!jQuery(this).hasClass('loaded')) {
                                let divider = new dividerShapes(this);
                                divider.initPoints();
                                jQuery(this).addClass('loaded');
                            }
                        });
        }
        // $e.commands.getAll();
        // 
    };
 
    elementorFrontend.hooks.addAction( 'frontend/element_ready/section', addSectionHandler );
    elementorFrontend.hooks.addAction( 'frontend/element_ready/column', addSectionHandler );
    elementorFrontend.hooks.addAction( 'frontend/element_ready/container', addSectionHandler );
});